mapper.select:
select count(*)
from board
where boardkind_canum=#{boardkind_canum}

mapper.update:
update board
set	readcount=readcount+1
where boardkind_canum=#{boardkind_canum} AND bnum=#{bnum}

mapper.select:
select *
from board
where boardkind_canum=#{boardkind_canum} AND bnum=#{bnum}

select bpath
from board
where boardkind_canum=#{boardkind_canum} AND bnum=#{bnum}
